void mainImage( out vec4 O, in vec2 SC ) 
{ 
    O = TEXF0(SC);
}

#define ALIVE true
#define DEAD  false
#define READ_CELL(P) (TEX0(TEX_COORDS(P)).r)

int read_neighbors_states(vec2 SC)
{
    /* Kernel:
                   t
                 l   r
                   b
    */

    float t  = READ_CELL(SC + vec2( 0,  1));
    float r  = READ_CELL(SC + vec2( 1,  0));
    float b  = READ_CELL(SC + vec2( 0, -1));
    float l  = READ_CELL(SC + vec2(-1,  0)); 
    float m  = READ_CELL(SC);

    return // max sum == 32
        (int(l == 0.0) << 4) +
        (int(b == 0.0) << 3) +
        (int(r == 0.0) << 2) +
        (int(t == 0.0) << 1) +
         int(m == 0.0);

}

void decompose_rule(
    in int rule,
    inout int[32] allowed_sums,
    inout int num_of_allowed_sums)
{
    const int num_of_neighbors = 5;
    const int num_of_combinations = 1 << num_of_neighbors;
    const int weights[5] = int[5]( 1,  2,  4,  8, 16 );
    int wIdx = 0;

    for (int combination = 0; combination < num_of_combinations; ++combination)
    {
        int sum = 0;

        if (((rule >> combination) & 1) == 0) { continue; }

        for (int b = 0; b < num_of_neighbors; ++b)
        {
            sum += weights[b] * ((combination >> b) & 1);
        }

        allowed_sums[wIdx] = sum;
        ++wIdx;
    }

    num_of_allowed_sums = wIdx;
}

bool check_rule(
    in vec2 TC,
    in int[32] allowed_sums,
    in int num_of_allowed_sums)
{
    int sum_of_neighbors_weights = read_neighbors_states(TC);

    bool result = false;

    for (int s = 0; s < num_of_allowed_sums; ++s)
    {
        if (sum_of_neighbors_weights == allowed_sums[s])
        {
            result = true;
            break;
        }
    }

    return result;
}

float init_one_pixel_in_center(vec2 SC)
{
    SC = floor(SC);
    vec2 M = vec2(floor(R.x * 0.5), floor(R.y * 0.5));
    return 
        SC.y == M.y && SC.x == M.x ?
            0.0 :
            1.0;
}


float randomize_part_of_frame(
    in vec2 SC,
    float amount)  // in range [0.0; 1.0]
{
    vec2 C = R.xy * 0.5;
    vec2 Offset = C * amount;

    float hash = step(fract(sin(dot(SC, vec2(127.1,311.7))) * 43758.5453123), 0.5);

    return 
       (SC.x > (C.x - Offset.x) &&
        SC.x < (C.x + Offset.x) &&
        SC.y > (C.y - Offset.y) &&
        SC.y < (C.y + Offset.y)) == true ?
            hash :
            1.0;
}

float mainScene(in vec2 SC)
{
    float transition_number = 0.0; 
    bool s = transition(iFrame, 100, transition_number);

    /* rules range = [0; 4294967295] or [0; 2^32 - 1] */
    int rule = 20000 + int(transition_number * 2.0 * 10.0);
    /* Only even rules used because odd rules is flickering. */

    int allowed_sums[32];
    int num_of_allowed_sums = 0;

    decompose_rule(rule, allowed_sums, num_of_allowed_sums);
    float state = 
        check_rule(SC, allowed_sums, num_of_allowed_sums) == ALIVE ?
        0.0 :
        1.0;

    return 
        s == true ?
            randomize_part_of_frame(SC, 0.4) :
            state;
}

void mainImage( out vec4 O, in vec2 SC )
{
    O = vec4(mainScene(SC));
}

#define R                        iResolution
#define TEX0(TC)               ( texture(iChannel0, (TC)) )
#define TEXF0(SC)              ( texelFetch(iChannel0, ivec2(SC), 0) )  // (channel, integer coordinates, mipmap level)
#define TEX_COORDS(SC)         ( (SC) / (R.xy) )                  // in range x=[0.0; 1.0], y=[0.0; 1.0]

/* Every num_of_frame returns true 
and number of detected transitions. */
bool transition(
    in int frame,
    in int numOfFrames,
    inout float quotent)
{
    // Convert to float.
    float f = float(frame);
    float inv = 1.0 / float(numOfFrames);

    // Calculate previous and current quotents.
    float qc = floor(f * inv);
    quotent = floor((f - 1.0) * inv);

    return quotent < qc;
}

uint hashUI32(uint value)
{
    // Pick some enthropy source values.
    // Try different values.
    const uint entropy0 = 12345u;
    const uint entropy1 = 67890u;

    // Calculate hash.
	value += entropy1;
	value *= 445593459u;
	value ^= entropy0;

    return value * value * value;
}
