void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 cc = fragCoord - iResolution.xy / vec2(2);
    float l = length(cc); //max(abs(cc.x), abs(cc.y));
    float scale = log(l) / l * 10.0 * (1.5 + sin(iTime));
    vec2 c = cc * scale;
    fragColor = any(equal(ivec2(c) % ivec2(10), ivec2(0))) ? vec4(1) : vec4(0);
    if (length(c) < 4.0) fragColor = vec4(0, 1, 0, 1);
}
