    float Circle(vec2 uv, vec2 position, float r, float blur) 
    {
        float d = length(uv - position);
        float c = smoothstep(r, r-blur, d);
        return c;
    }

void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
    vec2 uv = fragCoord.xy / iResolution.xy; // координаты от 0 до 1    
    uv -= .5;  // центр круга теперь в центре экрана; координаты от -0.5 до 0.5
    uv.x *= (iResolution.x / iResolution.y); 
    // head
    float c = Circle(uv, vec2(.0, .26), .2, .007);//head
    // eyes
    c -= Circle(uv, vec2(.1, .3), .06, .01); // right eye black  
    c -= Circle(uv, vec2(-.1, .3), .06, .01);  //left eye black    
    c += Circle(uv, vec2(.1, .3), .025, .01); //white circle in right eye      
    c += Circle(uv, vec2(-.1, .3), .025, .01); //white circle in left eye      
    //ears
    c += Circle(uv, vec2(.3, .26), .15, .005);//ear right
    c += Circle(uv, vec2(-.3, .26), .15, .005);//ear
    // тело
    c += Circle(uv, vec2(.0, -.18), .25, .005);
    //hands
    c += Circle(uv, vec2(.3, -.13), .08, .005);
    c += Circle(uv, vec2(-.3, -.13), .08, .005);
    //feet
    c += Circle(uv, vec2(.18, -.4), .09, .005);
    c += Circle(uv, vec2(-.18, -.4), .09, .005);    

   fragColor = vec4(vec3(c), 1.0);


}
