#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,q,r=iResolution,
    d=normalize(vec3((C-.5*r.xy)/r.y,1));  
    for(float i=0.,s,e,g=0.;
        ++i<70.;
        O.xyz+=mix(vec3(1),H(g*.1),.5)*.02*exp(-20.*i*i*e)
    )
    {
        p=g*d;
        p.z-=15.;
        p=R(p,normalize(vec3(3,2,1)),iTime*.5);        
        e=s=3.;
        for(int j=0;j++<10;)
            p.xz=abs(p.zx),
            e=min(
                e,
                (length(p.zx+length(p)-3.)-.3)/s
            ),
            q=R(normalize(vec3(1,2,3)),vec3(.577),iTime*.7),
            p=R(p,q,iTime*.3),
            p-=vec3(1,1.2,1.+sin(iTime)*.5),
            p*=1.2,
            s*=1.2;
        g+=e;
    }
}
