//Copyright Emanuel Acosta (emanvidmaker) 2022
float clmp(float v){
    return((v+1.0)/2.0);
}

float sdCircle(vec2 p, float s){
    return(length(p)-s);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 center = vec3 (0.5,0.5,0);
    float d = clmp(sdCircle(uv-center.xy,mod(iTime,2.0)));

    vec3 c1 = vec3(
   0,
    (d<0.5 && d > 0.49)? 1:0,
   (d<0.49 && d > 0.0 )? 1:0
    );

     d = clmp(sdCircle(uv-center.xy,mod(iTime+0.5,2.0)));
    vec3 c2 = vec3(
   0,
    (d<0.5 && d > 0.49)? 1:0,
   (d<0.49 && d > 0.0 )? 1:0
    );
    d = clmp(sdCircle(uv-center.xy,mod(iTime+1.1,2.0)));

   vec3 c3 = vec3(
   0,
    (d<0.5 && d > 0.49)? 1:0,
   (d<0.49 && d > 0.0 )? 1:0
    );

   vec3 col = (c1+c2+c3)/3.0;
    // Output to screen
    fragColor = vec4(col,1.0);
}
