void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // 得到 UV
    vec2 uv = fragCoord/iResolution.xy;

    // 通过对时间使用正弦函数，得到一个变化的值，处理后这个值处于[0.f, 1.f]
    float val = (sin(iTime) + 1.f) / 2.f;

    // 将进度值与反向的 UV.x 叠加，并向下取整，使得小于值的部分为 0.f，而值的部分为 1 .f
    float ans = floor(val + 1.f - uv.x);

    // 输出
    fragColor = vec4(vec3(ans), 1.f);
}
