void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 mouse = iMouse.xy/iResolution.xy;

    uv /= uv.y - 0.99;
    uv *= uv.x - 0.99;

    vec3 col = vec3(uv.x);
    col.r = cos(uv.x * 3.14 + iTime * 5.) + cos(uv.y * 3.14 + iTime);
    col.g = sin(uv.x * 3.14 + iTime * 2.) + sin(uv.y * 3.14 + iTime);
    col.b = tan(uv.x * 3.14 + iTime) + tan(uv.y * 3.14 + iTime);


    fragColor = vec4(col, 1.0);
}
