#define R                  iResolution
#define TEX_COORDS(P)     ( P / R.xy )                       // in range x=[0.0; 1.0], y=[0.0; 1.0]
#define NZMX             ( (iMouse.x+iMouse.x-R.x) / R.y )  // in range [-AR; AR]
#define NZMY             ( (iMouse.y+iMouse.y-R.y) / R.y )  // in range [-1.0; 1.0]
#define TEX0(x)          ( texture(iChannel0, x) )
#define LMB_IS_HOLD      ( iMouse.z > 0.0 )

vec2 shear(
    in vec2 P,
    in vec2 SHEAR)
{
    return vec2(
        P.x + SHEAR.x * P.y,
        P.y + SHEAR.y * P.x);
}

void mainImage( out vec4 O, in vec2 P )
{
    vec2 SHEAR = 
        LMB_IS_HOLD == true ?
            vec2(-NZMX, -NZMY) * 0.5 :
            vec2(0.0);

    P = shear(P, SHEAR);

    O = TEX0(TEX_COORDS(P));
}
