#define PI 3.141592

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/min(iResolution.x, iResolution.y);

    vec3 col = vec3(0.0);
    float angle = PI / 4.0;
    vec2 n = vec2(cos(angle), sin(angle));

    // cosine part
    float lambda = 0.01;
    float c = cos(dot(n, uv)/lambda + iTime * 10.0);

    // exponential part
    float sigma = 0.08;
    float e = exp(-1.0 * dot(uv, uv)/(sigma*sigma));

    vec3 bgColor = vec3(0.5, 0.5, 0.5);
    vec3 amp = vec3(0.5, 0.0, 0.0);

    col = bgColor + amp * c * e;

    fragColor = vec4(col,1.0);
}
