void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    float width = max(1.0, iResolution.x/iResolution.y);
    float height = max(1.0, iResolution.y/iResolution.x);
    vec2 coords = vec2(gl_FragCoord.x * width, (iResolution.y - gl_FragCoord.y)*height)/iResolution.xy;
    coords += vec2(cos(iTime*3.0),sin(iTime*2.0))*0.35;
    vec2 dist = coords - vec2(width/2.0, height/2.0);
    vec3 color = 1.0 - vec3(smoothstep(0.01, 0.011, dot(dist, dist)));

    fragColor = vec4(color,1.0);
}
