void mainImage( out vec4 FragColor, in vec2 FragCoord )
{
 	vec4 color = vec4(1., 1., 1., 1.);

    //vec4 color = texture2D(u_texture, v_texCoords.xy);
	vec2 pos = FragCoord.xy - 2. * iMouse.xy;

	float t = clamp((sin(iTime * .01 + pos.x * .01 + pos.y * .005) + 1.) / 2., 0., 1.);
	vec3 c = vec3(mix(0., 1., t), mix(.89, .39, t), mix(1., .85, t));

    FragColor = vec4(color.rgb * c.rgb, color.a);
}
