// (channel, pos, offset, scaling )
#define T(ch,u, ofs,s)  texture(ch,  ( (u)*s + ofs )   \
                                    / vec2( textureSize(ch,0) ) ) 

void mainImage( out vec4 O, vec2 u )
{
    u.y = mod(u.y, 400.);
    if ( u.y > 50. )                                //Â top: image
        O =   u.x < 180. ? T(iChannel0, u, 0., 2.)                         // precise mapping to pixels
            : u.x < 360. ? T(iChannel0, u-vec2(180,0),.5, 2. )             // offset = 1/2 pixel
                         : T(iChannel0, u-vec2(360,0), fract(iTime), 2. ); // offset = fract(time)
    else                                            // bottom: dense strips
        O =   u.x < 180. ? T(iChannel1, u, 0., 1.)
            : u.x < 360. ? T(iChannel1, u-vec2(180,0),.5, 1. )
                         : T(iChannel1, u-vec2(360,0), fract(iTime), 1. ),
         O = pow(O, vec4(1./2.2) );

}

// strip texture.  one band of 1 pixel width black/white, one band of 2 pixel width black/white 

void mainImage( out vec4 O, vec2 u )
{
    float x = u.x-.5;
    O = vec4( u.y > 25. ? mod(x,2.) : step(2.,mod(x,4.) ) );
}
