// color from
#define RAY_COL_FROM vec3(0.4, 0.9, 0.3)
// color to
#define RAY_COL_TO vec3(0.4, 1.0, 1.0)

// .0 ~ .5
#define BRIGHTNESS_FROM .0
#define BRIGHTNESS_TO .5
// color change time duration
#define DELTA 1.0

float distanceLine(vec2 uv, float r, float o) {
    return pow(abs(uv.x + r * uv.y - o - 0.5) / sqrt(2.0), 0.8);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uvs = fragCoord/iResolution.xy;

    float bloomr = .5;
    float del = mod(float(iTime), DELTA) / DELTA;
    vec3 col = mix(mix(RAY_COL_TO, RAY_COL_FROM, del), vec3(.0), clamp(.0, 1.0, pow(distanceLine(uvs, 0., 0.)/bloomr, mix(BRIGHTNESS_FROM, BRIGHTNESS_TO, del))));
    // Output to screen
    fragColor = vec4(col, 1.);
}
