// Not testes for uniformity, stratification, periodicity or whatever. Use (or not!) at your own risk


  const uint k = 1103515245U;  // GLIB C
//const uint k = 134775813U;   // Delphi and Turbo Pascal
//const uint k = 20170906U;    // Today's date (use three days ago's dateif you want a prime)
//const uint k = 1664525U;     // Numerical Recipes

vec3 uhash3( uvec3 x )         // iq version
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)/float(0xffffffffU);
}


vec3 hash( vec3 f )            // $FaN: vec3 to vec3. any scaling. f.z=0 seed is ok.
{ 
    return uhash3( floatBitsToUint(f) );
 // return uhash3( uvec3( floatBitsToUint(f.x),
 //                       floatBitsToUint(f.y),
 //                       floatBitsToUint(f.z) ) );
}
void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy/2.,
         U = ( 2.* u - R ) / R.y;

    O.rgb = hash(1e-5*vec3(U,iTime));  // histograms are ok whatever the scaling
 // O.rgb = hash(1e 5*vec3(U,iTime));
 // O.rgb = hash(     vec3(U,iTime));
 // O = vec4( hash(vec3(U,0)).r );     // 0 extra seed is ok

}
