//CLICK MOUSE AND MOVE


#define circle length(o-M)
#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

void mainImage(out vec4 c,in vec2 o){

  c=vec4(0);
  vec3 v,m1,m2,universe;
  vec2 M,R=iResolution.xy;o-=R/2.0;
  M=((iMouse.x+iMouse.y>0.0)?iMouse.xy:vec2(R.x/4.0,R.y/2.0)+1.0);
  float r=o.x*o.x+o.y*o.y,t=iTime*radians(180.0);r+=t;

  vec2 O1=(o+M-R/2.)*rot(0.3925);
  float g1=abs(O1.x)+abs(O1.y)+abs(O1.y*sqrt(2.)/2.0+O1.x*sqrt(2.)/2.0)+abs(-O1.y*sqrt(2.)/2.0+O1.x*sqrt(2.)/2.0);
  vec2 O2=(o-M+R/2.)*rot(0.3925);
  float g2=abs(O2.x)+abs(O2.y)+abs(O2.y*sqrt(2.)/2.0+O2.x*sqrt(2.)/2.0)+abs(-O2.y*sqrt(2.)/2.0+O2.x*sqrt(2.)/2.0);

  m1=sin(r)*vec3(0,1,1);
  m2=sin(r)*vec3(1,0,0);
  v=1.0-cos(r)*vec3(1,1,1);
  universe=cos(r)*vec3(1,1,1);

  c=vec4(universe,1);
  if(g1<640.0/2.0)c=vec4(clamp(m1*sin(t)*1.0+m2*cos(t)*1.0,0.,1.),1.);
  if(g2<640.0/2.0)c=vec4(clamp(m1*cos(t)*1.0+m2*sin(t)*1.0,0.,1.),1.);
  if(int(g1/32.0)==int(640.0/64.0))c=vec4(v,1);
  if(int(g2/32.0)==int(640.0/64.0))c=vec4(v,1);
  if(int(g1/10.0)==int(640.0/18.0))c=vec4(universe-0.75,1.0);
  if(int(g2/10.0)==int(640.0/18.0))c=vec4(universe-0.75,1.0);
  if(g1<640.0/2.0&&g2<640.0/2.0)c=vec4(universe-0.75,1.0);

}
