float calc(float colorSum, int range, float preColor){

    float result = abs(1.2*colorSum);

    return result;
}

vec3 kernel(ivec2 fragCoord){
    vec3 colorSum = vec3(0.);
    vec3 preColor = texelFetch(iChannel1, fragCoord, 0).rgb;
    int range = 1;
    mat3 m3 = mat3(
     0.565, -0.716,0.565,
     -0.716,0.627,-0.716,
     0.565,-0.716,0.565
    );

    vec3 c = vec3(0.,1.,0);


    for(int i = -range; i < range+1 ; i++ ){
        for(int j = -range; j < range+1 ; j++ ){
            colorSum += texelFetch(iChannel0, fragCoord + ivec2(i,j), 0).rgb * m3[j+1][i+1];
        }
    }
    c = vec3(
        calc(colorSum.r, range, preColor.r)
    //calc(colorSum.g, range, preColor.g),
    //calc(colorSum.b, range, preColor.b)
    );

    return c;
}

vec3 getColor(ivec2 fragCoord){
    vec3 num = kernel(fragCoord);
    return num;
}

vec2 N22(vec2 uv){
    vec3 a = fract(uv.xyx * vec3(213.34,234.34,345.65));
    a += dot(a, a+324.45);
    return fract(vec2(a.x*a.y, a.y*a.z));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0.);

    if(iFrame < 10){
        col = vec3(N22(0.1*uv).x);
    }else{
        col = getColor(ivec2(fragCoord));
    }

    fragColor = vec4(col,1);
}
