const vec2 c = vec2(-.79, .15); // Complex seed 

const vec4 colorPalette = -vec4(0,23,21,0);
const vec4 finalColor = vec4(.0);
const int hueModulus = 30;


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float zoom = exp(-iTime/10.0)*5.0;
    int recursion = int(exp(iTime)) + 200, i = 0;
    vec2 z = (fragCoord / iResolution.x - 0.5) * -zoom + -iMouse.xy/iResolution.xy;
    for(; i <= recursion && length(z) <= 2. ; i++ ) z = vec2(z.x*z.x-z.y*z.y, 2.0*z.x*z.y) + c;
    fragColor =  i - 1 == recursion ? finalColor : .6 + .6 * cos( 6.3 *  (float((i - 1) % hueModulus) / float(hueModulus)) + colorPalette);

}
