// оттенки заливки
vec3 brown1 () { return vec3(0.3961, 0.2627, 0.1294);}
vec3 brown2 () { return vec3(0.495, 0.37, 0.22);}
vec3 brown3 () { return vec3(0.3, 0.2, 0.1);}
vec3 white () {return vec3(0.9, 0.9, 0.7);}
vec3 black () {return vec3(0.0, 0.0, 0.0);}
vec3 red () {return vec3(0.590, 0.117, 0.106);}

// функция, возвращающая квадрат числа
float sq(float x) {return x*x;}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p= 1.0 - 2.0* fragCoord/iResolution.xy;
     float x = p.x;
     float y = p.y; 

    vec3 col = vec3(0.0, 0.0, 0.0);

    // голова, основная часть
    if (sq(x + 0.0) + 1.75*sq(y + 0.2) < 0.075) col=brown1();   
    // ушки, основная часть
    if (sq(x + 0.325) + sq(y + 0.225) < 0.025) col=brown1();
    if (sq(x - 0.325) + sq(y + 0.225) < 0.025) col=brown1();  
    // тело, основная часть 
    if (1.65*sq(x + 0.0) + sq(y - 0.23) < 0.08) col=brown1();
    // ножки основная часть    
    if (sq(x + 0.17) + 1.77*sq(y - 0.46) < 0.0145) col=brown1();
    if (sq(x - 0.17) + 1.77*sq(y - 0.46) < 0.0145) col=brown1(); 
    // ручки 
    if (sq(x + 0.2) + 1.75*sq(y - 0.1) < 0.014) col=brown1();
    if (sq(x - 0.2) + 1.75*sq(y - 0.1) < 0.014) col=brown1(); 
     // голова, светлая часть
     if (sq(x + 0.0) + 1.75*sq(y + 0.18) < 0.04) col=brown2();
     // тело, светлая часть
     if (1.1*sq(x + 0.0) + sq(y - 0.145) < 0.015) col=brown2();
     // ножки светлая часть
     if (sq(x + 0.19) + 1.77*sq(y - 0.461) < 0.0045) col=brown2();
     if (sq(x - 0.19) + 1.77*sq(y - 0.461) < 0.0045) col=brown2();   
     // ушки, тёмная часть
     if (sq(x + 0.325) + sq(y + 0.225) < 0.0125) col=brown3();
     if (sq(x - 0.325) + sq(y + 0.225) < 0.0125) col=brown3();    
      // глаза, белки 
      if (sq(x + 0.1) + sq(y + 0.2) < 0.00125) col=white();
      if (sq(x - 0.1) + sq(y + 0.2) < 0.00125) col=white();       
       // глаза, зрачки
       if (sq(x + 0.10 - 0.0102) + sq(y + 0.2) < 0.0006) col=black();
       if (sq(x - 0.10 - 0.0102) + sq(y + 0.2) < 0.0006) col=black();         
      // бровки 
      if (sq(x + 0.1) + 2.5*sq(y + 0.275) < 0.001) col=brown3();
      if (sq(x - 0.1) + 2.5*sq(y + 0.275) < 0.001) col=brown3();   
      // губы 
      if (0.55*abs(x) + abs(y + 0.093) < 0.02) col=red();
       if (0.45*sq(x) + sq(y + 0.11) < 0.00055) col=brown2(); 
      // носик
      if (0.9*abs(x) + abs(y + 0.155) < 0.022) col=black();    
       if (1.1*sq(x) + sq(y + 0.135) < 0.00057) col=brown2(); 

       // Вывод на экран
       fragColor = vec4(col,1.0);
}
