void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    float waitPixelRows = 200.;
    int speed = 1; // higher values will reduce quality
    int width = 3; 
    vec4 scanColor = vec4(0.4, 1., 0.85,1.);

    int scanPos = int(mod(float(iFrame * speed), iResolution.y + waitPixelRows));
    if(int(fragCoord.y) > int(iResolution.y) - scanPos)
        discard;
    else if(abs(int(fragCoord.y) + speed*width - (int(iResolution.y) - scanPos)) < width)
        fragColor = scanColor;
    else
        fragColor = texture(iChannel0, vec2(iResolution.x - fragCoord.x, fragCoord.y) / iResolution.xy);
}
