float sdBox( in vec2 p, in vec2 b )
{ 
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0)-0.005;
}

vec3 sdNote( in vec2 p, in vec2 b, in vec3 c, in float t )
{
    vec3 outline = vec3(1) * float(max(-sdBox(p, b - vec2(b.y)*0.15), sdBox(p, b)) <= 0.);
    vec3 box = c * float(sdBox(p, b * t) <= 0.);
    vec3 glow = c * (0.00001 / sdBox(p, b * t));
    return box + outline + glow;
}

vec3 cam(vec2 uv) {
