const float pi = 3.141592654;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -0.5 to 0.5)
    vec2 uv = fragCoord/iResolution.xy-0.5;

    // get angle in radians (-pi to pi)
    float ang = atan(uv.y, -uv.x);

    // convert radian angle to 0 to 1 range
    float brightness =  (ang + pi) / (2.0 * pi);

    // use the step command to create a moving threshold - step returns 0 or 1 depending on which side of the threshold it falls
    float mask = step(mod(iTime, 1.0), brightness);

    fragColor = vec4(mask, mask, mask, 0.0);
}
