const float PI = 3.14;

vec2 rotz(vec2 p, float a) {
    float c = cos(a);
    float s = sin(a);
    return mat2(c, -s, s, c) * p;
}

vec4 star(vec2 uv, vec4 c0, vec4 c1) {
    float z0 = smoothstep(0., 800., abs(1./(uv.x * uv.y)));
    float z1 = smoothstep(0., 4000., abs(1./(uv.x * uv.y)));
    return (c0 * z0 + c1 * z1) * abs(cos(iTime)) * .05/length(uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = ( 2. * fragCoord - iResolution.xy ) / iResolution.y;

    vec4 r0 = vec4(0.76, 0.24, 0.08, 1.);
    vec4 r1 = vec4(1., 0.83, 0.52, 1.);
    vec4 b0 = vec4(0.08, 0.24, 0.76, 1.);
    vec4 b1 = vec4(0.52, 0.83, 1., 1.);
    vec2 uv0 = rotz(uv - rotz(vec2(-0.5, 0.0), iTime), iTime + PI/4.);
    vec2 uv1 = rotz(uv - rotz(vec2(0.5, 0.0), iTime), iTime);

    fragColor = 0.3 * (star(uv0, r0, r1) + star(uv0, r0, r1)) + star(uv1, b0, b1) + star(uv1, b0, b1);
}
