// uniform wavespeed, frequency, amplitude

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float amp = 0.1;
    float freq = 10.5;
    float speed = 1.0/5.0;

    uv.x += iTime*speed;

    vec3 col = vec3(1.0, 1.0, 1.0);

    float y = smoothstep(0.0, 0.1, abs(uv.y - amp*sin(uv.x*freq)-0.5));

    y = step(0.1, y); // alter thickness of line to remove blur

    col = (1.0-y) * col + y*vec3(0.0, 0.0, 0.0);

    fragColor = vec4(col,1.0);
}
