void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 position = fragCoord.xy / iResolution.xy;
    float wave = sin(iTime * 5.0 + position.x * 20.0 + position.y * 30.0);
    fragColor = vec4(
        position.x,
        position.y,
        wave / 2.0 + 0.5, 1.0
    );
}
