float drawCircle(vec2 pos, float t)
{
    float dist = length(pos);

    float radius = mod(t * .1, .3);
    float fadeDis = smoothstep(.3, .0, radius);

    float intensity = clamp(pow(2./abs(dist-radius), .2), .0, 3.5);

    intensity *= smoothstep(2.2, 3.1, intensity);

    intensity *= fadeDis;

    return intensity;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    float intensity = drawCircle(pos, iTime);
    intensity += drawCircle(pos, iTime + 1.);
    intensity += drawCircle(pos, iTime + 2.);

    fragColor = vec4(.0,.3,.8, 1.) * intensity;
}
