void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float cx = iResolution.x/(2.0+0.65*sin(3.14*0.75*iTime));
    float cy = iResolution.y/(2.0+0.43*cos(3.14*0.51*iTime));

     // float x = 1.0/fragCoord.x;
     // float y = 1.0/fragCoord.y;


    float dist = sqrt((fragCoord.x-cx)*(fragCoord.x-cx)+(fragCoord.y-cy)*(fragCoord.y-cy));
    float pattern =sin(iTime)*sin(3.14*dist*iTimeDelta)+cos(dist+iTime);


    // Output to screen
    fragColor = vec4(sin(iTime)*pattern,cos(1.75*iTimeDelta)*pattern,sin(3.0*iTime)*pattern,1.0);
}
