float dist()
{
    return iMouse.z > 0.0 ? iMouse.x / iResolution.x : max(sin(iTime / 4.0 * 2.0 * 3.14), 0.0);
}

float threshold(in vec2 uv )
{
    return step(texture(iChannel0, uv).r, dist());
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = texture(iChannel1, uv).rgb * threshold(uv);

    // Output to screen
    fragColor = vec4(col, 1.0);
}
