vec3 palette2(in float t)
{
    vec3 a=vec3(.5,.5,.5);
    vec3 b=vec3(.5,.5,.5);
    vec3 c=vec3(1.,1.,1.);
    vec3 d=vec3(0.30,.20,.20);
    return a+b*cos(6.28318*(c*t+d));
}

vec2 complexNPower(vec2 z,float n){
    float r = length(z);
    float nr = pow(r,n);
    float theta = atan(z.y,z.x) * n;
    return vec2(nr * cos(theta),nr * sin(theta));
}

float escapeTime(float iter,vec2 z,vec2 c){
	float i;
	for(i=0.;i<iter;i++){
        z = complexNPower(z,2.) + c; // zÂ² + c
		if(dot(z,z)> 4.){
			break;
		}
	}
    return i-log2(log2(dot(z,z))) + 4.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=(2.*fragCoord.xy-iResolution.xy)/iResolution.x;
    vec2 uPos = vec2(0.,0.);
    float uScale = 3.;
	vec2 z=(uv+uPos)*uScale;
	vec2 c=vec2(-.77,.45);
	float iter=224.;
	float i=escapeTime(iter,z,c);

	vec3 color=palette2(i*iTime/100.);

	fragColor=vec4(color,1.);
}
