vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

mat2 rot(float c){
    float s=sin(c);
    return mat2(c=cos(c),s,-s,c);
}

float line(vec2 p,vec2 a,vec2 b){
    return length(clamp(dot(b-=a,p-=a)/dot(b,b),0.,1.)*b-p);
}

float sector(vec2 p,float r,float h){
    p = vec2(p.y,-p.x);
    float a = clamp(atan(p.y,p.x),-h,h);
    return line(p, vec2(0), vec2(cos(a),sin(a))*r );
}

void mainImage(out vec4 O,vec2 U){
    vec2 uv = (2.*U-iResolution.xy)/iResolution.y;
    uv.y++; 

    float t = iTime-.8;
    float a = atan(-uv.x,uv.y)+sin(t)*.1;


    O = vec4(0);
    float h = clamp(t*2.7-1.3,0.,1.);
    float f = 0.;
    if(t>.5) a /= h;
    float b = smoothstep(.2,1.2,texture(iChannel0, vec2(a*.2,a*.1+t*.05)).x);        

    f = sector(uv,t*12., .02 + h*3.1415926*.5);

    f = min(.002/(f*f),1.);

    if(t>.5) b *= f;
    b = .12+b*.7;

    if(t>0.) {
        f = mix(f,mix(.8,b,smoothstep(0.,1.,length(uv))),h*h*h);
    }
    else f = 0.;

    if(t<.5) f += smoothstep(max(sin((t+.3)*5.)*.1,0.),0.,b*dot(uv,uv*vec2(1,.3)));

    O.xyz = palette(clamp(f,0.,1.),vec3(-1,.55,0),vec3(2,.55,1.7),vec3(.5,.4,.1),vec3(.5,.5,.75));
    O = pow(O,vec4(1./2.2));
}
