void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float c = texelFetch(iChannel0, ivec2(fragCoord / Pixel_Scaling), 0).r; // get pixel from buffer A
    float t = texelFetch(iChannel1, ivec2(fragCoord), 0).r;  // get Tracer brightness 
    vec3 col = hue(t * 3.) * t * TRACER_BRIGHTNESS;  // get tracer color.
    col += c; 
    fragColor = vec4(col, 1.);
}

#define PI 3.14159265359
#define hue(v) ( .6 + .6 * cos( 2.*PI*(v) + vec3(0,-2.*PI/3.,2.*PI/3.) ) )

// settings.
#define Pixel_Scaling 4.  // pixel size  (may need reset if changed)
#define Time_Scale 2  // frames to skip animation. 0 or 1 is full fps, 2 is every 2nd frame, 3 is every 3rd, and on.
#define Brush_Size 2.
#define TRACER_DECAY 1.5
#define TRACER_BRIGHTNESS 0.2



float hash11(float p)// random stuff
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

bool evaluateFrame()
{
    if (Time_Scale == 0)
        return true;
    else if (iFrame % Time_Scale != 0)
        return false;
    else
        return true;
}

float getRandomPixelState(vec2 pos)
{
    return step(.5, hash11(pos.x * pos.y * fract(iDate.x + iDate.y + iDate.z + iDate.w)));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 bufferRes = iResolution.xy / Pixel_Scaling;  // Get buffer resolution
    if (fragCoord.x > bufferRes.x || fragCoord.y > bufferRes.y)  // check if pixel is within buffer.           
          return;


    if (iFrame == 0 || texelFetch(iChannel1, ivec2(82,0),0).x > 0.)  // Randomize pixels on first frame or R is pressed.
    {        
        fragColor = vec4(getRandomPixelState(fragCoord));        
        return;        
    } 

    vec4 Mus = iMouse;   // Brush 
    if (Mus.z > 0.)  
    {                        
        if (length((Mus.xy / Pixel_Scaling) - fragCoord) < Brush_Size)
        {            
            fragColor = vec4(1.); 
            return;
        }

    }

    ivec2 coord = ivec2(fragCoord);  // convert coords to interger coordanate.

    if (!evaluateFrame())  // check if we should animate this frame or skip.
    {           
        fragColor = texelFetch(iChannel0, coord, 0).rgba;                  
        return;   
    }


    float neighborState = 0.;
    float thisPixelState = 0.;
    for(int x = -1; x <= 1; x++)  // nearby pixels life status
    {
        for(int y = -1; y <= 1; y++)
        {
            if (x == 0 && y == 0)  // if its this pixel store that value
            {
                thisPixelState =  texelFetch(iChannel0, coord, 0).r;                
            }
            else // otherwise if neighbor is alive add to counter.
            {
                neighborState +=  texelFetch(iChannel0, coord+ ivec2(x,y), 0).r;
            }

        }

    }

    // Conway's Game of Life rules applied
    float c =0.; 
    if (thisPixelState == 0.) // if pixel is dead
    {
        if (neighborState == 3.) // become alive if 3 neighbors are alive.
            c = 1.;
    }
    else if (neighborState == 2. || neighborState == 3.) // if pixel is alive and has 2 or 3 alive neighbors
            c = 1.;                                      // then stay alive otherwise pixel is dead (default value); 


    fragColor = vec4(c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    ivec2 coord = ivec2(fragCoord);  // convert coordanates to integer coordanate.
    float c = 0.;

    // Tracers
    for(int x = -1; x <= 1; x++)  // combine values of adjecent pixels.
    {
        for(int y = -1; y <= 1; y++)
        {
            c += texelFetch(iChannel1, coord + ivec2(x,y)  , 0).r;
        }
    }   
    c *= 0.11111111111; // calculate avarage.
    c *= max(0., 1. - (iTimeDelta * TRACER_DECAY)); // decay (darken).


    // Add value from Buffer A and clamp to max 2.    
    c = min(1.5, (c + texelFetch(iChannel0, ivec2(fragCoord / Pixel_Scaling), 0).r)); 

    fragColor = vec4(c);
}
