void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //uv.x *= 800.0/450.0;

    float value = texture(iChannel1, vec2(0.0, iTime)).r*(sin(iTime*3.0)/2.0+0.5);

    vec2 uv_r = uv;
    vec2 uv_g = uv;
    vec2 uv_b = uv;

    uv_r.x+=value*0.2;
    uv_b.x-=value*0.5;

    // Time varying pixel color
    float r = texture(iChannel0, uv_r).r;
    float g = texture(iChannel0, uv_g).g;
    float b = texture(iChannel0, uv_b).b;

    vec3 col = vec3(r,g,b)+vec3(texture(iChannel2, uv*3.0).r*value*1.0);
    // Output to screen
    fragColor = vec4(col,1.0);
}
