/*------------------------
Settings
------------------------*/
#define crossfadeLength 1.
#define textureZoom .3

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;

    // Mouse
    float mouse = iMouse.x/iResolution.x;

    // Looped Time
    float time = sin(iTime) * .5 + .5;

    // Progress
    float progress = time; /* time || mouse */
    progress *= (1. + crossfadeLength);
    progress -= crossfadeLength;

    // Mask
    float mask = smoothstep(uv.x - crossfadeLength, uv.x, progress);
    float noise = texture(iChannel0, uv * textureZoom).b;
    noise *= mask;
    noise += mask;
    noise = clamp(noise, 0., 1.);

    // Textures
    vec3 texture1 = texture(iChannel1, uv).rgb;
    vec3 texture2 = vec3(0.);

    // Mix
    vec3 col = mix(texture1, texture2, noise);
    fragColor = vec4(col, 1.0);
}
