#define ITERATIONS 1000

vec2 scale(vec2 x){
    x -= iResolution.xy/2.;
    x /= iResolution.y;
    x *= 2.;
    return x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 z = scale(fragCoord);
    vec4 m = vec4(scale(iMouse.xy), iMouse.zw);


    if (m.z > 0.0) {
        int i = 0;
        while (i < ITERATIONS) {
            z = vec2(z.x*z.x - z.y*z.y + m.x, 2. * z.x * z.y + m.y);
            i++;
            if (length(z) > 2.) break;
        }

        float t = float(i)/float(ITERATIONS);

        fragColor = 
          vec4(2.0, 0., 0., 0.) / (1.0 + pow(2.0, pow(2.0 - 100.0* t, 2.0)))
        + vec4(0., 0., 15., 1.) * (t * pow(1. - t, 9.0)) 
        + vec4(0., 20., 0., 1.) * (t * t * (1. - t)) 
        + vec4(1., 1., 1., 1.)  * t;
    }

    vec2 c = scale(fragCoord);
    z = c;
    int i = 0;
    while (i < ITERATIONS) {
        z = vec2(z.x*z.x - z.y*z.y + c.x, 2. * z.x * z.y + c.y);
        i++;
        if (length(z) > 2.) break;
    }

    float t = float(i)/float(ITERATIONS);

    if (i == ITERATIONS) {
        fragColor -= vec4(.2,.2,.2,0.0);
    }
}
