//hsv2rgb
// from https://www.shadertoy.com/view/MsS3Wc
// https://www.youtube.com/c/InigoQuilez
// https://iquilezles.org
vec3 hsv2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

	return c.z * mix( vec3(1.0), rgb, c.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = 2.0*fragCoord/iResolution.xy-1.0;
    float aspect = iResolution.x/iResolution.y;
    p.x *= aspect;

    vec2 c = (2.0*iMouse.xy/iResolution.xy-1.0);
    c.x *= aspect;

    float f = 0.0;


    f += smoothstep(0.5, 0.25, length(p));
    f += smoothstep(0.5, 0.25, length(p-c));
    //f +=  0.25*smoothstep(0.4, 0.5, length(p+p-c));
    //f +=  0.25*smoothstep(0.4, 0.5, max(length(p),length(p-c)));
    f += smoothstep(1.0, 0.5, length(p) + length(p-c));

    vec3 col = hsv2rgb(vec3(f, f, f));

    fragColor = vec4(col, 1.0);
}
