void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 sp = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    vec3 co = vec3(5. * sin(.5*iTime) + cos(3.*iTime), .5*cos(2.*iTime)+.75*sin(1.5*iTime + .2), 5. * cos(.5*iTime));
    vec3 ct = vec3(0);

    vec3 cam_ww = normalize(ct - co);
    vec3 cam_uu = normalize(cross(cam_ww, vec3(0,1,0)));
    vec3 cam_vv = cross(cam_uu, cam_ww);

    vec3 rd = sp.x * cam_uu + sp.y * cam_vv + 1.0 * cam_ww;
    vec3 col = vec3(0);

    float tot_dist = 0.0;

    for (int st = 0; st < 512; ++st) {
        vec3 pos = co + tot_dist * rd;        

        vec3 q = (mod(pos, 3.) - 1.5) / 3.;
        float h = length(q) - 0.2;        
        if (h < 0.0001) {
            vec3 N = normalize(q);
            col = 0.5 * N + vec3(0.5);
            float ee = exp(-.15*tot_dist);
            col *= ee;
            col *= pow(dot(N, rd),2.0);

            col += ee*clamp(1.0 - pow(abs(dot(N, rd)),5.0), 0.0, 1.0)*texture(iChannel0, reflect(rd, N)).rgb;

            break;
        }
        tot_dist += h;
    }

    col = sqrt(col);

    fragColor = vec4(col,1.0);
}
