// https://www.youtube.com/watch?v=xh61Ol2X4GA

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Get color from bufferA
    vec3 col = texture(iChannel0, uv).xyz;

    // Output to screen
    fragColor = vec4(col,1.0);
}

int getNeighbors(ivec2 coord){
    int nums = 0;

    for (int x = -1; x <= 1; x++) {
        for (int y = -1; y <= 1; y++) {
            if (x==0 && y==0) continue;
            float r = texelFetch(iChannel1, coord + ivec2(x, y), 0).r;
            if (r > 0.5) {
                // Neighbors alive
                nums++;
            }
        }
    }
    return nums;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    vec3 color = vec3(0.0);

    if (iFrame < 10) {
        // Initialize color
        color = texture(iChannel0, uv).xyz;
    } else {

        // Use last bufferA
        bool alive = texelFetch(iChannel1, ivec2(fragCoord), 0).r > 0.5;

        // Get neighbors
        int neighbors = getNeighbors(ivec2(fragCoord));
        float r = 0.0;
        if (alive && (neighbors==2 || neighbors==3)) {
            r = 1.0;
        } else if (!alive && neighbors==3) {
            r = 1.0;
        } else {
            r = 0.0;
        }

        color = vec3(r);
    }

    fragColor = vec4(color,1.0);
}
