#define pi acos(-1.)
#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
float smin(float a, float b, float k){
  float h = max(0., k-abs(a-b)) / k;
  return min(a,b) - h*h*k*.25;
}

float t;
float m(vec2 uv, inout float id){
   float E_1 = -50., E_2 = 10., E_3 = 55.;
  //float noice = smoothstep(0., 5., abs(texture(iChannel0, vec2(0.01)).x*45.));
  float E = mod(t*sin(t*.125)*.06-.06, 3.);
  uv *= (E <= 1. ? E_1 : E <= 2. ? E_2 : E <= 3. ? E_3 : E_1);
  float rep = 16.;
  uv *= length(vec3(uv, 1.))-sin(uv.x*uv.x*.234+t*.45)*sin(uv.y*uv.y*.234+t*.34)*.5-.5;

  uv += t*2.625; // fract(sin(dot(uv,vec2(347.45,465.6)+t)*565.)*.000000000055)
  uv *= rot(t*.125);

  vec2 gid = floor(uv/rep-.5);
  id = fract(sin(dot(gid, vec2(.34,.454)))*345.);
  uv = (fract(uv/rep-.5)-.5)*rep;
  for(int i = 0; i < 4; i++){

    uv = abs(uv)-2.;
    uv *= rot(t*id+uv.x);
    uv = vec2(smin(uv.x, 1.66-uv.x, 1.), smin(uv.y, 1.63-uv.y, 1.));


  }
  return pow(dot(uv,uv)-.1, 2.2525);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{t = mod(iTime+5., 100.);
	vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv /= vec2(iResolution.y/iResolution.x, 1.);
  vec3 co = vec3(0.);
  float id;
  if(m(uv,id) < .25/pi){
    co += .85;
    co *= 1.-length(id+uv)*.134;
    co *= vec3(id)*vec3(.343,.45,.465)*2.-uv.x*uv.x;
  }
	fragColor = vec4(co, 1.);
}
