void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    float aspectRatio = iResolution.y / iResolution.x;
    uv.y *= aspectRatio;
    uv *= 64.0;

    float timeMultiplier = abs(32.0 - floor(uv.x)) + abs(16.0 - floor(uv.y));

    uv = fract(uv);

    float x = uv.x;
    float y = uv.y;

    // cycle at 1 second
    float time = fract(iTime * timeMultiplier / 8.0);

    float yOffset = 0.0;
    float xOffset = 0.0;
    //float yOffset = min(2.0 * time, 1.0) / 2.0;
    //float xOffset = (max(2.0 * time, 1.0) - 1.0) / 2.0;
    yOffset = smoothstep(0.0, 1.0, 2.0 * time) / 2.0;
    xOffset = smoothstep(0.0, 1.0, 2.0 * (time - 0.5)) / 2.0;

    float rLowerY = 0.5 - yOffset;
    float rUpperY = 1.0 - yOffset;
    float rLowerX = 0.5 - xOffset;
    float rUpperX = 1.0 - xOffset;

    if (y > rLowerY && y < rUpperY && x > rLowerX && x < rUpperX) {
        fragColor = vec4(1.0);
        return;
    }

    float lLowerY = 0.0 + yOffset;
    float lUpperY = 0.5 + yOffset;
    float lLowerX = 0.0 + xOffset;
    float lUpperX = 0.5 + xOffset;

    if (y > lLowerY && y < lUpperY && x > lLowerX && x < lUpperX) {
        fragColor = vec4(1.0);
        return;
    }

    fragColor = vec4(0.0);
}
