#define A(u) texture(iChannel0,(u)/iResolution.xy)
uint nor(uint a, uint b)
{
    return ~(a|b);
}
uint deep5(uint a, uint b, uint c, uint d, uint e)
{
    uint r1 = 0U;
    uint r2 = 0U;
    {
        uint e = nor( a , a );
        uint f = nor( b , b );
        uint g = nor( d , d );
        uint h = nor( a , b );
        uint i = nor( e , f );
        uint j = nor( h , i );
        uint k = nor( j , c );
        uint l = nor( j , k );
        uint m = nor( c , k );
        uint n = nor( l , m );
        uint o = nor( n , i );
        uint p = nor( n , d );
        uint q = nor( o , g );
             r1= nor( p , q );
    }
    {
        uint o1 = nor(a,b);  uint o0 = o1;
        uint o2 = nor(o1,a); 
        uint o3 = nor(o1,b);
             a  = nor(o2,o3);
             b  = c;
             o1 = nor(a,b);
             o2 = nor(o1,a);
             o3 = nor(o1,b);
        uint o4 = nor(o2,o3);
        uint o5 = nor(o4,o4);
        uint d2 = nor(d,d);
             o5 = nor(o5,d2);
             o4 = nor(o4,o0);
             o4 = nor(o4,o4);
             o4 = nor(o4,d);
             r2 = nor(o4,o5);
    }
    uint ee = nor(e,e);
         r1 = nor( r1 , ee);
         r2 = nor( r2 , e);
    uint o  = nor( r1 , r2);
         o  = nor( o , o);
    return o;
}
void mainImage( out vec4 fragColor, in vec2 u )
{
    uint a = uint(A(u+vec2( 1, 0)).x) & 1U;
    uint b = uint(A(u+vec2( 0, 1)).x) & 1U;
    uint c = uint(A(u+vec2(-1, 0)).x) & 1U;
    uint d = uint(A(u+vec2( 0,-1)).x) & 1U;
    uint e = uint(A(u+vec2( 1, 1)).x) & 1U;
    uint f = uint(A(u+vec2(-1, 1)).x) & 1U;
    uint g = uint(A(u+vec2( 1,-1)).x) & 1U;
    uint h = uint(A(u+vec2(-1,-1)).x) & 1U;
    uint i = uint(A(u+vec2( 0, 0)).x) & 1U;    //i = nor(i,i);

    uint j = deep5(a,b,c,d,i);  //j = nor(j,j);
    uint k = deep5(e,f,g,h,j);  //k = nor(k,k);

    uint o = k;
    if(iFrame==0||iMouse.z>.5)
    {
        vec2 v = 1.*(u        *2.-iResolution.xy)/iResolution.y;
        vec2 m = 1.*(iMouse.xy*2.-iResolution.xy)/iResolution.y;
        o = uint(fract(cos(dot(u,vec2(234.76543,iTime)))*2467.5678)+.5);
        o = uint(step(dot(v,v),dot(m,m)*.06));
    }
    fragColor = vec4(o);
}
