void mainImage( out vec4 O, in vec2 U )
{
  U*=U;
  ivec2 i=ivec2(U);
  float t=iTime*3.23,c=mod(float(fract(t)<.5?i.x^i.y:i.x|i.y)*floor(t),256.)/256.;

  O = vec4(c,c,c,1.0);
}

vec2 mainSound( int samp, float time )
{
    int t=samp>>4;t*=3;//int(.1814*float(samp));
    t=(t*16/3|t*3)*(t>>12&t>>7)|(t>>5&t>>3);
    return vec2( mod(float(t),256.)/1024.-.125 );
}
