void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //shifting 0,0 to center so range comes in [-1,1]
    vec2 n_uv = (uv - 0.5) * 2.0;

    //creating a blurred black-gray circle with circle equation
    float res = (n_uv.x * n_uv.x) + (n_uv.y * n_uv.y);

    //making circle white
    res = 1.0 - res;

    //making gray values white
    res =  sign(res) * (res/res);

    //create vec3 color
    vec3 col = vec3(res);

    // Output to screen
    fragColor = vec4(col,1.0);
}
