void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv.y = uv.y * uv.y;
    uv.x = uv.y * uv.y;

    uv.y += sin(iTime);
    uv.y = mod(uv.y, 0.1);

    uv.y = step(0.04, uv.y);

    if(uv.y < 0.01){
        fragColor = vec4(uv.x);
        return;
    }

    vec3 col = vec3(uv.y);


    fragColor = uv.y *  vec4(uv + sin(iTime), 0.0, 1.0);
}
