void mainImage( out vec4 O, in vec2 u )
{
    u=(u- iResolution.xy*.5)/iResolution.y*3.-.5;
    float n= 8.+mod(floor(u.x)*3.+floor(u.y),15.);
    u=fract(u)-.5;
    vec3 col =vec3(1.)*smoothstep(.495,.48,max(abs(u.x),abs(u.y)));
    for(float j=0.;j<1.;j+=.2) for(float i=0.;i<n;i+=1.){
        float t=6.283/(n)*i;
        vec3 r1=vec3(.199,.202,.205)+sin(iTime)*0.01+j,
             r2=vec3(1.,.6,.4)+j-r1;
        vec2  p=vec2(sin(t),cos(t));
        col*=-1.+2.* smoothstep(r2,r2+.01, r1*length(u-p));
    }
    O = vec4(col*.5+.5,1.0);
}



