#define pi acos(-1.)
//https://iquilezles.org/articles/palettes/
vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 2.*pi*(c*t+d) );
}


float noise(vec3 a)
{
    return fract(sin(dot(a,vec3(132.341242,3.432432,17.34242)))*1435.432432);
}
vec3 Saw(vec3 x,vec3 d)
{
 vec3 disp = x+d;
 return 2.0* abs(disp - round(disp));
}
vec3 RemapFrom01(vec3 x, float l, float h)
{
 return l + x *(h-l);
}
vec3 ss( vec3 x )
{
  return x*x*(3.-2.0*x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = fragCoord/iResolution.xy;
   float video = texture(iChannel0,uv).r;
   //video=uv.x;
   vec3 saw = Saw(vec3(video),vec3(0.5,1.-1./6.,1./6.));
   //saw = RemapFrom01(saw,-1./2.,1.); // add this bit if you want all the channels to add up to max 1
   saw = clamp(saw,0.,1.);
   //saw = ss(saw); //adding a smoothstep to the linear version makes it approximate the cosine version
   vec3 rainbow = palette(video,vec3(.5),vec3(.5),vec3(1.),vec3(0.,1./3.,2./3.));
   fragColor = vec4(uv.y>.5?rainbow:saw,0);

}
