#define BIAS 0.5

vec4 HueShift (in vec3 Color, in float Shift)
{
    vec3 P = vec3(0.55735)*dot(vec3(0.55735),Color);

    vec3 U = Color-P;

    vec3 V = cross(vec3(0.55735),U);    

    Color = U*cos(Shift*6.2832) + V*sin(Shift*6.2832) + P;

    return vec4(Color,1.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 correction = vec2(iResolution.x/iResolution.y, 1.);

    uv *= correction;
    uv += (correction-1.)/-2.;

    vec2 disp = uv-.5;
    float dist = length(disp);

    float mask = 1. - clamp(ceil(dist - BIAS), .0, 1.);

    // Time varying pixel color
    vec2 noise = ((texture(iChannel1, uv+iTime*vec2(.05, -.077) ).xy) - .5 )* 2.;
    vec3 col = vec3(HueShift(vec3(uv, texture(iChannel0,uv + noise).x), dist*5.));
    col*=mask;

    // Output to screen
    //fragColor = vec4((texture(iChannel0,uv + noise).xyz),1.0);
    //fragColor = vec4(vec3(texture(iChannel0,uv + noise).x),1.0);
    //fragColor = vec4(vec3(texture(iChannel0,uv + .0 + (disp*speed)).x),1.0);
    //fragColor = vec4(noise, 0., .0);
    //fragColor = vec4((disp), .0,1.0);
    fragColor = vec4(col, 1.);
}
