void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
//Transitioning
float t = min(1.0,floor(sin(iTime)) * sin(iTime) * 2.0);

//Pulse
float ft = min(1.0,fract(iTime * 2.));


 vec3 p1 = vec3(1.,1.,1.);
 vec3 p2 = vec3(1.,0.,0.);
vec3 p2f = vec3(0.,0.,0.);

     vec3 col = mix(p1 , mix(p2,p2f,ft), t);
    fragColor = vec4(col,1.0);
}
