void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord - iResolution.xy) /iResolution.y;
    vec2 mouse = iMouse.xy / iResolution.xy;
    vec3 color = vec3(0.0);

    uv = mouse == vec2(0.0) ? uv - vec2(1.0) * (iResolution.xy / iResolution.y) : uv;

    vec3 endColor = vec3(0.3, 0.1, 0.6);

    uv *= 5.0;
    // mouse -1 to 1
    vec2 animOffset = vec2(sin(iTime * 0.15), cos(iTime * 0.15)) * 2.0 - 1.0;
    vec2 mouseOffset = mouse != vec2(0.0) ? mouse * 2.0 - 1.0 : animOffset;
    float n = dot(abs(uv), mouseOffset);

    // bg circles
    vec2 offset = vec2(sin(iTime + n), cos(iTime + n));
    float d = length(abs(fract(uv + offset)) - 0.5) - 0.5;
    d = smoothstep(0.03, 0.0, abs(d));

    // draw circles
    color += vec3(d);

    // move space to mouse
    uv -= (mouse * 2.0 - 1.0) * 5.0 * (iResolution.xy / iResolution.y);
    // make circle at mouse
    float l = length(uv) - 0.1;

    color += mix(vec3(1.0), endColor, l);

    fragColor = vec4(color, 1.0);
}
