void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float rule = (0.5 - mod(iTime * 0.5, 1.0));
    float diff = length(uv - 0.5) * 0.675 - abs(rule);
    if(diff > 0.0)
    {
        float scale = clamp(0.0, 0.04, 0.04 - diff) / 0.04;
        if(rule > 0.0)
            fragColor = texture(iChannel0, uv);
        else
            fragColor = texture(iChannel1, uv);
        fragColor *= scale;
    }
    else
    {
        if(rule > 0.0)
            fragColor = texture(iChannel0, uv);
        else
            fragColor = texture(iChannel1, uv);
    }
}
