vec4 print(vec4 rgba, vec2 xy, int s, int n) {
    float fix=0.0,index;
    xy.x/=4.0;xy.y/=4.0;xy.x-=5.0;
    if(mod(xy.x,1.0)>0.5&&mod(xy.x,1.0)<0.75){xy.x=-1.0;}
    if(mod(xy.y,1.0)>0.5&&mod(xy.y,1.0)<0.75){xy.y=0.0;}
    int[] chars=int[10](
        glyphs[38],glyphs[38],
        glyphs[38],glyphs[38],
        glyphs[38],glyphs[38],
        glyphs[38],glyphs[38],
        glyphs[38],glyphs[38]
    );
    if(n==1){chars[0]=glyphs[1];}
    else if(n==0){chars[0]=glyphs[0];}
    else{
        if(mod(log16(n),1.0)==0.0){fix+=1.0;}
        index=float(int(float(n)/pow(16.0,digits(n)+fix-ceil(xy.x/10.0)))&0xf);
        if(int(xy.x)/10>=int(digits(n)+fix))chars[int(xy.x)/10]=glyphs[38];
        else chars[int(xy.x)/10]=glyphs[int(index)];
    }
    if(mod(xy.y,10.)>=5.&&mod(xy.y,10.)<10.&&mod(xy.x,6.)>=0.&&mod(xy.x,10.)<5.&&int(xy.y)/10==s)
    { float wave=float(chars[int(xy.x)/10]>>((int(mod(xy.y,5.))*5)+(4-int(mod(xy.x,5.))))&1);
      rgba=abs(rgba+vec4(wave,wave,wave,1));
    }return rgba;
}



