// a simple shader based on this chapter of the book of shaders: https://thebookofshaders.com/10/

// Many thanks to Patricio Gonzalez Vivo & Jen Lowe for their tutorials on thebookofshaders.com.
// Their website is an invaluable resource when starting out with learning shaders and has helped me greatly

float SinRand(float seed)
{
	return fract(sin(seed)* 10000000.0);
}

// the following three functions were taken from https://thebookofshaders.com/edit.php#10/ikeda-00.frag
float Randf(float Seed)
{
	return fract(sin(Seed)* 10000000000000000000000000000000.0);
}

float Rand2D(vec2 Coord)
{
	return fract(sin(dot(Coord.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

float randomSerie(float x, float freq, float t) {
    return step(.8,Randf( floor(x*freq)-floor(t) ));
}

float Pixelize(vec2 Coord, float ScaleFactor)
{
	vec2 Position = floor(Coord * ScaleFactor);
	return Rand2D(Position);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 color;

	vec2 FragCoord = fragCoord.xy/iResolution.xy;
	FragCoord.x *= iResolution.x/iResolution.y;

	color = vec3(Pixelize(FragCoord, sin(iTime/4.0) * 20.0));

	fragColor = vec4(color, 1.0);
}
